package util;

import java.io.File;
import java.io.IOException;

import javax.swing.ImageIcon;

import org.apache.commons.io.FileUtils;

import com.sun.org.apache.xerces.internal.impl.dv.util.Base64;

public class StaticIcon extends ImageIcon {
    private static final long serialVersionUID = -5040220439874039951L;

    public static String putioTreeIcon = "";
    public static String putioIcon = "";
    public static String putioActiveIcon = "";
    public static String openFolderIcon = "";
    public static String openFolderSelIcon = "";
    public static String closedFolderIcon = "";
    public static String closedFolderSelIcon = "";
    public static String disconnectIcon = "";
    public static String connectIcon = "";
    public static String refreshIcon = "";
    public static String settingsIcon = "";
    public static String pauseIcon = "";
    public static String playIcon = "";
    public static String stopIcon = "";
    public static String exitIcon = "";
    public static String miniPlayIcon = "";
    public static String miniPlayGreyIcon = "";
    public static String miniPauseIcon = "";
    public static String miniPauseGreyIcon = "";
    public static String miniStopIcon = "";
    public static String miniStopGreyIcon = "";
    public static String miniDelIcon = "";
    public static String miniDelGreyIcon = "";
    public static String miniRetryIcon = "";
    public static String miniRetryGreyIcon = "";

    public StaticIcon( String icon ) {
        super( Base64.decode( icon ) );
    }

    public static String getBytesFromFile( File file ) {
        String ret = null;
        try {
            ret = Base64.encode( FileUtils.readFileToByteArray( file ) );
        }
        catch ( IOException e ) {
            e.printStackTrace();
        }
        return ret;
    }
}
